import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Card } from 'react-bootstrap';
import { useLazyQuery, useMutation } from '@apollo/client';
import { gql } from '@apollo/client';
import Auth from '../utils/auth';
import '../styles/searchBooks.css';

const SEARCH_BOOKS = gql`
  query searchBooks($searchTerm: String!) {
    searchBooks(searchTerm: $searchTerm) {
      books {
        bookId
        title
        authors
        description
        image
        link
      }
    }
  }
`;

const SAVE_BOOK = gql`
  mutation SaveBook($bookId: String!, $title: String!, $authors: [String], $description: String, $image: String, $link: String) {
    saveBook(book: {
      bookId: $bookId,
      title: $title,
      authors: $authors,
      description: $description,
      image: $image,
      link: $link
    }) {
      _id
      savedBooks {
        bookId
        title
      }
    }
  }
`;

const SearchBooks = () => {
  // ... existing state
  const [showChat, setShowChat] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');

  // Chat component
  const ChatBox = () => (
    <div className="chat-container" style={{
      position: 'fixed',
      bottom: '20px',
      right: '20px',
      width: '300px',
      height: showChat ? '400px' : '60px',
      background: 'white',
      borderRadius: '10px',
      boxShadow: '0 0 10px rgba(0,0,0,0.1)',
      transition: 'all 0.3s ease',
      zIndex: 1000
    }}>
      {/* Chat header */}
      <div className="chat-header" style={{
        padding: '15px',
        background: '#28a745',
        color: 'white',
        borderRadius: '10px 10px 0 0',
        cursor: 'pointer'
      }} onClick={() => setShowChat(!showChat)}>
        <h5 style={{ margin: 0 }}>Book Assistant</h5>
      </div>

      {showChat && (
        <>
          {/* Chat messages */}
          <div className="chat-messages" style={{
            height: '280px',
            overflowY: 'auto',
            padding: '15px'
          }}>
            {chatMessages.map((msg, index) => (
              <div key={index} className={`message ${msg.sender}`} style={{
                marginBottom: '10px',
                padding: '8px 12px',
                borderRadius: '15px',
                maxWidth: '80%',
                backgroundColor: msg.sender === 'user' ? '#e9ecef' : '#28a745',
                color: msg.sender === 'user' ? 'black' : 'white',
                marginLeft: msg.sender === 'user' ? 'auto' : '0'
              }}>
                {msg.text}
              </div>
            ))}
          </div>

          {/* Chat input */}
          <div className="chat-input" style={{
            padding: '15px',
            borderTop: '1px solid #dee2e6'
          }}>
            <form onSubmit={handleChatSubmit} style={{
              display: 'flex',
              gap: '10px'
            }}>
              <input
                type="text"
                value={chatInput}
                onChange={(e) => setChatInput(e.target.value)}
                placeholder="Ask about books..."
                style={{
                  flex: 1,
                  padding: '8px',
                  borderRadius: '20px',
                  border: '1px solid #dee2e6'
                }}
              />
              <button type="submit" style={{
                background: '#28a745',
                border: 'none',
                borderRadius: '20px',
                padding: '8px 15px',
                color: 'white'
              }}>
                Send
              </button>
            </form>
          </div>
        </>
      )}
    </div>
  );

  const handleChatSubmit = async (e) => {
    e.preventDefault();
    if (!chatInput.trim()) return;

    // Add user message
    setChatMessages(prev => [...prev, { text: chatInput, sender: 'user' }]);
    
    // Here you would integrate with your AI service
    // For now, let's add a mock response
    setTimeout(() => {
      setChatMessages(prev => [...prev, {
        text: "I can help you find books and provide information about them. What would you like to know?",
        sender: 'bot'
      }]);
    }, 1000);

    setChatInput('');
  };

const SearchBooks = () => {
  const [searchInput, setSearchInput] = useState('');
  const [searchBooks, { loading, data }] = useLazyQuery(SEARCH_BOOKS);
  const [saveBook] = useMutation(SAVE_BOOK);

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    try {
      await searchBooks({
        variables: { searchTerm: searchInput }
      });
    } catch (err) {
      console.error('Search error:', err);
    }
  };

  const handleSaveBook = async (book) => {
    try {
      const { data } = await saveBook({
        variables: {
          bookId: book.bookId,
          title: book.title,
          authors: book.authors,
          description: book.description,
          image: book.image,
          link: book.link
        }
      });
      console.log('Book saved:', data);
    } catch (err) {
      console.error('Save error:', err);
    }
  };

  return (
    <>
      <div className="search-hero">
        <Container>
          <h1>Discover Your Next Book</h1>
          <Form onSubmit={handleFormSubmit}>
            <Row className="justify-content-center">
              <Col md={8} lg={6}>
                <div className="search-input-wrapper">
                  <Form.Control
                    name='searchInput'
                    value={searchInput}
                    onChange={(e) => setSearchInput(e.target.value)}
                    type='text'
                    placeholder='Search by title, author, or keyword...'
                  />
                  <Button type='submit' className="search-button">
                    Search
                  </Button>
                </div>
              </Col>
            </Row>
          </Form>
        </Container>
      </div>

      <Container className="book-grid">
        <h2>
          {loading 
            ? 'Loading...' 
            : data?.searchBooks.books?.length 
              ? `Viewing ${data.searchBooks.books.length} results:` 
              : 'Which book is on your mind today?'}
        </h2>
        <Row>
          {data?.searchBooks.books?.map((book) => (
            <Col md={4} lg={3} key={book.bookId}>
              <div className="book-card">
                {book.image && (
                  <img 
                    src={book.image} 
                    alt={`Cover for ${book.title}`} 
                    className="book-image"
                  />
                )}
               <div className="book-info">
						<h3 className="book-title">{book.title}</h3>
						<p className="book-authors">By: {book.authors?.join(', ')}</p>
						<p className="book-description">{book.description}</p>
						{Auth.loggedIn() && (
							<Button
								className="save-button"
								onClick={() => handleSaveBook(book)}
							>
								Save
							</Button>
                  )}
                </div>
              </div>
            </Col>
          ))}
        </Row>
      </Container>
      <ChatBox/>
    </>

  );
};
}
export default SearchBooks;
