import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Card } from 'react-bootstrap';
import { useLazyQuery, useMutation } from '@apollo/client';
import { gql } from '@apollo/client';
import Auth from '../utils/auth';

// Define GraphQL queries and mutations
const SEARCH_BOOKS = gql`
  query searchBooks($searchTerm: String!) {
    searchBooks(searchTerm: $searchTerm) {
      books {
        bookId
        title
        authors
        description
        image
        link
      }
      totalCount
    }
  }
`;

const SAVE_BOOK = gql`
  mutation saveBook($book: SaveBookInput!) {
    saveBook(book: $book) {
      username
      savedBooks {
        bookId
        title
      }
    }
  }
`;

const SearchBooks = () => {
  const [searchInput, setSearchInput] = useState('');
  const [searchBooks, { loading, data }] = useLazyQuery(SEARCH_BOOKS);
  const [saveBook] = useMutation(SAVE_BOOK);

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    if (!searchInput) return;

    try {
      await searchBooks({
        variables: { searchTerm: searchInput }
      });
    } catch (err) {
      console.error('Search error:', err);
    }
  };

  const handleSaveBook = async (bookData) => {
    if (!Auth.loggedIn()) {
      return;
    }

    try {
      await saveBook({
        variables: { book: bookData }
      });
      alert('Book saved successfully!');
    } catch (err) {
      console.error('Save error:', err);
    }
  };

  return (
    <>
      <div className="text-light bg-dark p-5">
        <Container>
          <h1>Search for Books!</h1>
          <Form onSubmit={handleFormSubmit}>
            <Row>
              <Col xs={12} md={8}>
                <Form.Control
                  name='searchInput'
                  value={searchInput}
                  onChange={(e) => setSearchInput(e.target.value)}
                  type='text'
                  size='lg'
                  placeholder='Search for a book'
                />
              </Col>
              <Col xs={12} md={4}>
                <Button type='submit' variant='success' size='lg'>
                  Submit Search
                </Button>
              </Col>
            </Row>
          </Form>
        </Container>
      </div>

      <Container>
        <h2 className='pt-5'>
          {loading 
            ? 'Loading...'
            : data?.searchBooks.books.length
              ? `Viewing ${data.searchBooks.books.length} results:`
              : 'Search for a book to begin'}
        </h2>
        <Row>
          {data?.searchBooks.books.map((book) => {
            return (
              <Col md="4" key={book.bookId} className="mb-4">
                <Card border='dark'>
                  {book.image && (
                    <Card.Img 
                      src={book.image} 
                      alt={`The cover for ${book.title}`}
                      variant='top'
                    />
                  )}
                  <Card.Body>
                    <Card.Title>{book.title}</Card.Title>
                    <p className='small'>Authors: {book.authors?.join(', ')}</p>
                    <Card.Text>{book.description}</Card.Text>
                    {Auth.loggedIn() && (
                      <Button
                        className='btn-block btn-info'
                        onClick={() => handleSaveBook(book)}
                      >
                        Save this Book!
                      </Button>
                    )}
                  </Card.Body>
                </Card>
              </Col>
            );
          })}
        </Row>
      </Container>
    </>
  );
};

export default SearchBooks;
